insert into mb_post(uid, content, images_url, create_time) values("1159", "【食物让你从头补到脚】眼睛：鸡蛋防白内障，胡萝卜防眼干；大脑：圆白菜健脑、抗抑郁；皮肤：西红柿防晒保湿；鼻子：葵花子预防鼻炎；嘴唇：核桃滋润补水；牙齿：芒果、猕猴桃、虾护好牙龈；头发：牛奶、南瓜养发；胸部：菜花预防乳腺癌；心脏：芦笋、紫葡萄护心；肠胃：话梅、西梅、豆豉改善消化。", "http://ts3-image.b0.upaiyun.com/2012/1203/17/50bc6c7567944.jpg!100x100.cut.jpg", "2015-04-08 06-44-39");
