insert into mb_post(uid, content, images_url, create_time) values("1159", "【温开水保健养生法】1、漱口，洗除口腔异味；2、冲胃肠，早晨喝杯温开水，清理体内残留物；3、刷牙，保护牙龈以及牙口神经；4、温润通喉道，大口吞咽，冲下残留物；5、解醉酒，冲淡稀释酒精，保护人体肝脏，补偿因为醉酒呕吐失却的大量水分。", "http://ts3-image.b0.upaiyun.com/2012/1127/17/50b4840d3fcef.jpg!100x100.cut.jpg", "2015-04-08 06-44-39");
