insert into mb_post(uid, content, images_url, create_time) values("1159", "【养肝护肝，食疗5个小妙招】1.适当食用葡萄糖、蜂蜜等易于消化的单糖与双糖食物，增加肝糖原储备；2.常饮菊花玫瑰花等饮品，可消食解腻、平肝明目；3.常吃核桃仁、开心果等坚果，可疏肝理气；4.每周吃1次畜禽肝脏，“以肝养肝”；5.忌食酒精和刺激性食品。", "http://ts3-image.b0.upaiyun.com/2012/1110/14/509df1a135bf8.jpeg!100x100.cut.jpg", "2015-04-08 06-44-39");
