insert into mb_post(uid, content, images_url, create_time) values("1159", "【排酸肉真的抗癌吗？】“排酸肉”是肉品的加工工艺的一类。有着“营养物质易消化、口感好、味道好”等特点。但现在有很多如：“排毒、保健、抗癌”等夸大的宣传。希望仅此为大家认清“排酸肉”的好处，但也不过分听信没有依据的宣传！从而做到理性选择，健康饮食!", "http://ts3-image.b0.upaiyun.com/2012/1110/14/509df01676dcc.jpeg!100x100.cut.jpg", "2015-04-08 06-44-39");
